const express = require('express');
const app = express();
const port = 3000;

app.get('/', (req, res) => {
  res.send(`
    <!DOCTYPE html>
    <html>
    <head>
        <title>My First Node.js Web Page</title>
        <style>
            body {
                background-color: #fafafa;
                font-family: Arial, sans-serif;
                margin: 0;
                padding: 20px;
            }
        </style>
    </head>
    <body>
        <h1>Welcome to my web page!</h1>
        <p>This is a simple webpage served by a Node.js server.</p>
    </body>
    </html>
  `);
});

app.listen(port, () => {
  console.log(`Example app listening at http://localhost:${port}`)
});
